import requests
from bs4 import BeautifulSoup
def get_movies():
	headers = {
	'user-agent':'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/53.0.2785.89 Safari/537.36',
	'host':'www.zxcs.me'
	}
	book_list = []

	link = 'http://www.zxcs.me/'
	r = requests.get(link,headers=headers,timeout=20)
	#print(str(i),'页面响应状态码：',r.status_code)

	soup=BeautifulSoup(r.text,'lxml')
	div_list = soup.find_all('div',class_='box')                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
	for each in div_list:
	    booknames=each.find_all('a')
	    for bookname in booknames:
	    	book=bookname.text.strip()
	    	book_list.append(book)
	return book_list
movies = get_movies()	
print(movies)

